mapper.select:
select

from example_as_val
where VALSET_ID = #{valsetId,jdbcType=VARCHAR}
  and VAL_ID = #{valId,jdbcType=VARCHAR}

mapper.delete:
delete from example_as_val
where VALSET_ID = #{valsetId,jdbcType=VARCHAR}
  and VAL_ID = #{valId,jdbcType=VARCHAR}

mapper.insert:
insert into example_as_val (VALSET_ID, VAL_ID, VAL,
  ORD_INDEX, LSTDATE, IS_SYSTEM
  )
values (#{valsetId,jdbcType=VARCHAR}, #{valId,jdbcType=VARCHAR}, #{val,jdbcType=VARCHAR},
  #{ordIndex,jdbcType=INTEGER}, #{lstdate,jdbcType=TIMESTAMP}, #{isSystem,jdbcType=CHAR}
  )

mapper.update:
update example_as_val
set VAL = #{val,jdbcType=VARCHAR},
  ORD_INDEX = #{ordIndex,jdbcType=INTEGER},
  LSTDATE = #{lstdate,jdbcType=TIMESTAMP},
  IS_SYSTEM = #{isSystem,jdbcType=CHAR}
where VALSET_ID = #{valsetId,jdbcType=VARCHAR}
  and VAL_ID = #{valId,jdbcType=VARCHAR}

mapper.select:
select VAL_ID as VALUE, VAL as NAME from example_as_val
where 1=1
  AND valset_id=#{valsetId,jdbcType=VARCHAR}

select *
from example_as_val where valset_id = "SSSFM_ITEM_CODE_ID" and VAL_ID in

  #{item,jdbcType=VARCHAR}
