mapper.select:
SELECT t.ID, t.ORDERCODE, t.ORDERNAME, t.SUPPLIER, t.supplierName, t.type, t.purchasing, t.purchasingGroup, t.voucherDate, CASE WHEN t.type='0' THEN '投诉工单' WHEN t.type='1' THEN '对账工单' ELSE '已办结' END AS type_name, t.APPROVALSTATE, CASE WHEN t.APPROVALSTATE='0' THEN '未提交' WHEN t.APPROVALSTATE='1' THEN '已提交' END AS approvalState_name, t.CONFIRMSTATE, CASE WHEN t.CONFIRMSTATE='0' THEN '未确认' WHEN t.CONFIRMSTATE='1' THEN '已确认' WHEN t.CONFIRMSTATE='2' THEN '拒绝' END AS confirmState_name, t.CLOSESTATE, CASE WHEN t.CLOSESTATE='0' THEN '未关闭' WHEN t.CLOSESTATE='1' THEN '已关闭' END AS closeState_name, t.remark, t.version, t.create_Time, t.create_User, t.last_Modified, t.ts, t.dr FROM SANY_ORDER t WHERE 1=1
and t.ORDERCODE like CONCAT(CONCAT('%', ${condition.searchMap.orderCode}),'%')
and t.supplierName like CONCAT(CONCAT('%', ${condition.searchMap.supplierName}),'%')
and t.voucherDate between to_date(${condition.searchMap.startTime},'yyyy-mm-dd hh24:mi:ss') and to_date(${condition.searchMap.endTime},'yyyy-mm-dd hh24:mi:ss')
and t.type = ${condition.searchMap.type}
and t.APPROVALSTATE = ${condition.searchMap.APPROVALSTATE}
and t.CONFIRMSTATE = ${condition.searchMap.CONFIRMSTATE}
and t.CLOSESTATE = ${condition.searchMap.CLOSESTATE}
order by
${item.property} ${item.direction}
