mapper.select:
SELECT t.id, t.code, t.name, t.type, CASE WHEN t.type='0' THEN '投诉工单' WHEN t.type='1' THEN '对账工单' ELSE '已办结' END AS type_name, t.content, t.applicant, t.applyTime, t.finishTime, t.status, CASE WHEN t.status='0' THEN '未提交' WHEN t.status='1' THEN '已提交' WHEN t.status='2' THEN '审批中' WHEN t.status='0' THEN '已办结' END AS status_name, t.remark, t.version, t.createTime, t.createUser, t.lastModified, t.lastModified, t.ts, t.dr FROM example_workorder t WHERE 1=1
and id = #{id, jdbcType=VARCHAR}
order by
${item.property} ${item.direction}

mapper.delete:
DELETE FROM example_workorder
WHERE 1=1

		and 1=2



			and id = #{condition.id}


			and id in (

					#{item,jdbcType=VARCHAR}

			)
